#include <iostream>using namespace std;void Nhap(int[], int&);bool ktHoanThien(int);int HoanThienDau(int[], int);int main(){	int a[100];	int n;	Nhap(a, n);	cout << "\nSo hoan thien dau tien cua mang la: " << HoanThienDau(a, n);	return 1;}void Nhap(int a[], int& n){	do	{		cout << "Nhap so phan tu mang: ";		cin >> n;		if (n < 0)			cout << "Nhap lai" << endl;	} while (n < 0);	for (int i = 0; i < n; i++)	{		cout << "Nhap a[" << i << "]: ";		cin >> a[i];	}}bool ktHoanThien(int k){	int s = 0;	for (int i = 1; i < k; i++)		if (k % i == 0)			s = s + i;	if (s == k)		return true;	return false;}int HoanThienDau(int a[], int n){	for (int i = 0; i < n; i++)		if (ktHoanThien(a[i]))			return a[i];	return -1;}