#include <iostream>#include <iomanip>using namespace std;void Nhap(int[], int&);int ktCon(int[], int, int[], int);void KetLuan(int[], int&, int[], int&);int main(){	int a[100], b[100];	int n, m;	Nhap(a, n);	Nhap(b, m);	KetLuan(a, n, b, m);	return 1;}void Nhap(int a[], int& n){	do	{		cout << "Nhap so phan tu mang: ";		cin >> n;		if (n < 0)			cout << "Nhap lai" << endl;	} while (n < 0);	for (int i = 0; i < n; i++)	{		cout << "Nhap a[" << i << "]: ";		cin >> a[i];	}}int ktCon(int a[], int n, int b[], int m){	if (n > m)	{		return 0;	}	int flag = 0;	for (int vt = 0; vt <= m - n; vt++)	{		int co = 1;		for (int i = 0; i < n; i++)			if (a[i] != b[vt + i])				co = 0;		if (co == 1)			flag = 1;	}	return flag;}void KetLuan(int a[], int& n, int b[], int& m){	int kl = ktCon(a, n, b, m);	if (kl == 1) {		cout << "\nMang A la mang con cua mang B";		return;	}	cout << "\nMang A ko la mang con cua mang B";}