#include <iostream>#include <iomanip>using namespace std;void Nhap(int[], int&);int TongCon(int[], int, int, int);void TimCon(int[], int, int);int main(){	int a[100];	int n,M;	Nhap(a, n);	cout << "\nNhap gia tri M: ";	cin >> M;	cout << "\n Day so co tong = M cua mang co (vi tri) va (so phan tu) la: ";	TimCon(a, n, M);	return 1;}void Nhap(int a[], int& n){	do	{		cout << "Nhap so phan tu mang: ";		cin >> n;		if (n < 0)			cout << "Nhap lai" << endl;	} while (n < 0);	for (int i = 0; i < n; i++)	{		cout << "Nhap a[" << i << "]: ";		cin >> a[i];	}}int TongCon(int a[], int n, int vt, int l){	int s = 0;	for (int i = 0; i <= l - 1; i++)		s = s + a[vt + i];	return s;}void TimCon(int a[], int n, int M){	int vtd, vtc;	for (int l = 1; l <= n; l++)		for (int vt = 0; vt <= n - l; vt++)			if (TongCon(a, n, vt, l) == M)			{				cout << vt << setw(6) << l;				vtd = vt;				vtc = vt + l - 1;				return;		    }	vtd = vtc = -1;}